		dp[i] = 0;
		for(int j=1; j<=min(i-1, A-1); j++){
			ll s, cnt = 0;
			if(j==i-1){ dp[i] = (dp[i] + dpb[max(0, j-2)]) % mod; continue; }
			if(i-(j+B-1)<=A-1){
				if(i-j-B+1<=0){
					int l = min(A-1, i-j-1);
					cnt = (cnt + sum2[l-1]+1)%mod;
