					int l = min(i-j-1, A-1);
					cnt = (cnt + sum2[l-1] + mod - sum2[i-j-B]+dpb[i-j-B])%mod;
				}
			}
			if(i-j-B+1 < 0) s = sum[i-j-1];
			else s = (sum[i-j-1] + mod - sum[i-j-B+1] + dp[i-j-B+1]) % mod;
			cnt = (cnt + s) % mod;
			if(j==1) dp[i] = (dp[i] + cnt) % mod;
			else dp[i] = (dp[i] + dpb[j-2]*cnt) % mod;
		}
