					f[i][k] = add(f[i][k], f[i - j][k ^ 1]);
				}
		f[0][0] = 0;
		for (int i = 1; i < n; ++i) {
			if (i < a) g[i][0] = 1;
			for (int j = 1; j < std::min(i, a); ++j)
				g[i][0] = add(g[i][0], g[i - j][1]);
			if (i < b)
				g[i][1] = add(f[i - 1][0], f[i - 1][1]);
			for (int j = 1; j < std::min(i, b); ++j) {
				int coef = j == 1 ? 1 : add(f[j - 2][0], f[j - 2][1]);
				g[i][1] = add(g[i][1], mul(g[i - j][0], coef));
			}
			if (n - i < b)
				ans = add(ans, mul(g[i][0], add(f[n - i - 1][0], f[n - i - 1][1])));
			if (n - i < a)
				ans = add(ans, g[i][1]);
		}
	}
	int tot = 1;
	for (int i = 1; i <= n; ++i) tot = add(tot, tot);
	printf("%d\n", add(tot, mod - ans));
}

