    int n, a, b;
    scanf("%d%d%d", &n, &a, &b);
    if(a > b) swap(a, b);
    dp1[0][0] = 1;
    for(int j=0;j<=n;j++) sum1[0][j] = 1;
    for(int i=1;i<=n;i++){
        for(int j=1;j<=a-1;j++) dp0[i][0] = (dp0[i][0] + sum1[i-j][n])%mod;
        for(int j=0;j<=b-a-1;j++) dp2[i][j+a] = (dp2[i][j+a] + dp1[i-a][j])%mod;

        for(int j=0;j<b-1;j++){
            dp2[i][j+1] = (dp2[i][j+1] + dp2[i-1][j])%mod;
        }
        for(int j=0;j<b-1;j++){
            dp1[i][j+1] = (dp1[i][j+1] + dp0[i-1][j])%mod;
            dp1[i][j+1] = (dp1[i][j+1] + dp1[i-1][j])%mod;
            dp1[i][j+1] = (dp1[i][j+1] + dp2[i-1][j])%mod;
        }

        for(int j=0;j<=n;j++){
            if(j == 0) sum1[i][j] = dp1[i][j];
            else sum1[i][j] = (sum1[i][j-1] + dp1[i][j])%mod;
        }
    }
    int ans = 1;
    for(int i=1;i<=n;i++) ans = ans*2%mod;
    for(int j=0;j<=n;j++){
        ans = (ans - dp0[n][j] + mod)%mod;
