    //    res += func(i + k + 1, i + k + A, false);
    //}

    if (k >= B) {
        return 0_mi;
    }
    auto& memo = dp[i][k];
    if (used[i][k]) {
        return memo;
    }
    used[i][k] = true;

    memo = func(i + k + 1, i + k + A, false);
    memo += func_sum(i, k + 1);
