inline void add(int &x, int y) { x += y; if(x >= mod) x -= mod; }
int main()
{
	get3(n, a, b);
	if(a > b) std::swap(a, b);
	if(a <= b)
	{
		dp[0][0][0] = dp[0][0][1] = 1;
		for(int i=0; i<=n; i++)
		{
			if(i > 0)
			{
				for(int j=0; j<=i; j++) for(int tp=0; tp<2; tp++)
				{
					add(pre1[i][j][tp], pre1[i-1][j][tp]);
					dp[i][j][tp] = pre1[i][j][tp];
				}
				add(pre2[i], pre2[i-1]);
				add(dp[i][i][0], pre2[i]);
			}

			for(int j=0; j<=i; j++) for(int tp=0; tp<2; tp++) if(dp[i][j][tp])
			{
				// dp[i][j][tp] -> dp[i+k][j][tp ^ 1]
				int lb = i + (tp == 1 ? a : 1), rb = std::min(n, j + b - 1);
				// printf("i= %d j= %d tp= %d dp= %d\n", i, j, tp, dp[i][j][tp]);
				if(lb <= rb)
				{
					add(pre1[lb][j][tp^1], dp[i][j][tp]);
					add(pre1[rb+1][j][tp^1], mod - dp[i][j][tp]);
				}

				if(tp == 1)
				{
					int lb = i + 1, rb = std::min(n, i + a - 1);
					// if(i == 2) printf("j= %d tp= %d lb= %d rb= %d\n", j, tp, lb, rb);
					if(lb <= rb)
					{
						add(pre2[lb], dp[i][j][tp]);
						add(pre2[rb+1], mod - dp[i][j][tp]);
					}
				}
			}
		}
		int ans = 0;
		for(int i=1; i<=n; i++) for(int j=0; j<2; j++) add(ans, dp[n][i][j]);
		ans = (qpow(2, n) + mod - ans) % mod;
		printendl(ans);
	}
	return 0;
}

