template<long long Mod>
struct mint{
    long long x;
    mint(long long x=0) :x((x% Mod+Mod)%Mod){}

    mint operator-() const { return mint(-x); }

    mint& operator+=(const mint a) {
        if((x += a.x) >= Mod) x -= Mod;
        return *this;
    }
    mint& operator-=(const mint a) {
        if((x += Mod-a.x) >= Mod) x -= Mod;
        return *this;
    }
    mint& operator*=(const mint a) {
        (x *= a.x) %= Mod;
        return *this;
    }

    mint operator^(long long t) const {
        mint ret(1);
        mint a = *this;
        while(t){
            if(t&1){
                ret *= a;
            }
            a *= a;
            t >>= 1;
        }
        return ret;
    }
    mint inv() const { return *this ^ (Mod-2); }
    mint& operator/=(const mint a) { return *this *= a.inv(); }

    mint operator+(const mint a) const { return mint(*this) += a; }
    mint operator-(const mint a) const { return mint(*this) -= a; }
    mint operator*(const mint a) const { return mint(*this) *= a; }
    mint operator/(const mint a) const { return mint(*this) /= a; }

    friend istream& operator>>(istream& is, mint& a) { return is >> a.x; }
    friend ostream& operator<<(ostream& os, const mint& a) { return os << a.x; }
};

int N, A, B;

bool visA[5001][2];
mint<MOD> dpA[5001][2];
mint<MOD> countA(int L, int k=0){
    if(L == 0){
        if(k) return 1;
        else return -1;
    }
    if(L < A) return 0;
    if(visA[L][k]) return dpA[L][k];
    visA[L][k] = true;
    mint<MOD> ret = 0;
    for(int i=1; i<=A && L-i>=0; i++){
        ret += countA(L-i, k^1);
    }
    return dpA[L][k] = ret;
}

bool visB[5001];
mint<MOD> dpB[5001];
mint<MOD> countB(int L){
    if(L <= 0){
        return 1;
    }
    if(visB[L]) return dpB[L];
    visB[L] = true;
    mint<MOD> ret = countB(L-1);
    for(int i=B; L-i>=0; i++){
        ret += countB(L-i-1);
    }
    return dpB[L] = ret;
}

bool vis[5001][2];
mint<MOD> dp[5001][2];
mint<MOD> solve(int l, int a){
    if(l == N){
        if(a == 0) return 0;
        return mint<MOD>(-1);
    }
    if(vis[l][a]) return dp[l][a];
    vis[l][a] = true;
    mint<MOD> ret = solve(l+1, a)*2;
    for(int i=A; l+i<=N; i++){
        ret -= solve(l+i, 1)*countA(i)*countB(i);
    }
    return dp[l][a] = ret;
}

