void get(int N,int A,int B){
	int a=0,b=0;
	for(int i=0;i<(1<<N);i++){
		string s = "";
		for(int j=0;j<N;j++){
			if((i>>j)&1)s += '0';
			else s += '1';
		}

		for(int j=0;j<N;j++){
			if(j+A-1>=N)break;
			bool f = true;
			for(int k=0;k<A;k++){
				if(s[j+k]!='0'){
					f=false;
					break;
				}
			}
			if(f){
				for(int k=j;k<N;k++){
					if(s[k]=='0')s[k]='1';
					else break;
				}
			}
		}
		bool f = false;
		for(int j=0;j<N;j++){
			if(j+B-1>=N)break;
			bool F = true;
			for(int k=0;k<B;k++){
				if(s[j+k]!='1'){
					F=false;
					break;
				}
			}
			if(F)f=true;
		}
		if(!f){
			if(i%2==0)a++;
			else b++;
		}
	}

	cout<<a<<','<<b<<endl;
}

