    for(LL i = 1; i < b+1; ++i){
        inv[i] = ModInt(i).inverse();
    }
    for(LL a_cnt = 0; a_cnt <= b; ++a_cnt){
        ModInt cnt = 1;
        LL base = 1 + (a+1)*a_cnt;
        for(LL len = base; len <= b; ++len){
            part_cnt[len] += cnt;
            cnt *= ModInt(a_cnt*2 + 1 + len - base);
