template<uint_fast64_t Modulus = MOD>
struct Modint {
  using u64 = uint_fast64_t;
  u64 a;

  constexpr Modint(const u64 x = 0) noexcept : a(x % Modulus) {}

  constexpr Modint operator+(const Modint rhs) const noexcept {
    return Modint(*this) += rhs;
  }
  constexpr Modint operator-(const Modint rhs) const noexcept {
    return Modint(*this) -= rhs;
  }
  constexpr Modint operator*(const Modint rhs) const noexcept {
    return Modint(*this) *= rhs;
  }
  constexpr Modint operator/(const Modint rhs) const noexcept {
    return Modint(*this) /= rhs;
  }

  constexpr Modint &operator+=(const Modint rhs) noexcept {
    a += rhs.a;
    if (a >= Modulus) a -= Modulus;
    return *this;
  }
  constexpr Modint &operator-=(const Modint rhs) noexcept {
    if (a < rhs.a) a += Modulus;
    a -= rhs.a;
    return *this;
  }
  constexpr Modint &operator*=(const Modint rhs) noexcept {
    a = a * rhs.a % Modulus;
    return *this;
  }
  constexpr Modint &operator/=(Modint rhs) noexcept {
    u64 exp = Modulus - 2;
    while (exp) {
      if (exp & 1) *this *= rhs;
      rhs *= rhs;
      exp >>= 1;
    }
    return *this;
  }

  Modint pow(u64 t) const {
    if (!t) return 1;
    Modint x = pow(t>>1);
    x *= x;
    if (t&1) x *= *this;
    return x;
  }

  explicit operator bool() const {
    return a;
  }

  friend ostream &operator<<(ostream &os, const Modint &m) {
    return os << m.a;
  }
};

using mint = Modint<>;

void solve() {
  int n, a, b; cin >> n >> a >> b;
  if (a > b) swap(a, b);

  if (a == 1) {
    cout << modpow(2, n) << endl;
    return;
  }

  vector<mint> s(b, 1);
  rep1(i,b) {
    REP(j,a+1,b-i-1) {
      s[i+j] += s[i] * (j - a);
    }
  }

  vector<mint> ss(b, 0);
  REP(i,a+1,b-1) {
    ss[i] = ss[i-1] + s[i-a];
  }

  Graph<mint> dp(n+1, vector<mint>(2, 0));
  rep(i,b) {
    dp[i][0] = s[i] + ss[i];
  }

  rep(i,n) {
    rep1(j,a) {
      if (i+j > n) break;
      dp[i+j][1] += dp[i][0];
    }
    rep1(j,b) {
      if (i+j > n) break;
      dp[i+j][0] += dp[i][1] * s[j];
    }
  }

  mint ans = mint(2).pow(n);
  rep(i,b) {
    ans -= dp[n-i][1] * (s[i] + ss[i]);
  }
  cout << ans << endl;
}


/* -------- <programs end> -------- */


signed main() {
  cin.tie(nullptr);
  ios::sync_with_stdio(false);
  cout << fixed << setprecision(10);
  solve();
  return 0;
}

