			for (int ones = 1; ones < b; ones++) {
				rez += dp(n - ones, 0, false);
			}
			rez += dpsum(n - b, l + b);
		}
		rez += dp(n - 1, l + 1, true);
	}
	mem[can_one][n][l] = rez.value;
	return rez;
}

