// state: len, cnt, last
// last = 1 ->
//   len + 1, cnt + 1, 1
//   len + 1, cnt + 1, 0
// last = 0 ->
//   len + A, cnt + A, 1
//   len + 1, cnt + 1, 0
//   len + k + 1, 1, 0 for k = 1...A - 1

typedef modnum<int(1E9 + 7)> mint;
mint dp[5010][5010][2];
int main() {
  int n, a, b;
  cin >> n >> a >> b;
  if (a > b) swap(a, b);
  for (int i = 0; i < b; ++i) {
    for (int j = 0; j < 2; ++j) {
      dp[n][i][j] = 1;
    }
  }

  for (int i = n - 1; i >= 0; --i) {
    mint val = 0;
    for (int c = 1; c <= a - 1; ++c) {
      if (i + c + 1 <= n) {
        val += dp[i + c + 1][1][0];
      }
    }
    if (n - i < a) {
      val += dp[n][1][0];
    }

    for (int j = 0; j < b; ++j) {
      for (int k = 0; k < 2; ++k) {
        if (k == 1) {
          if (j + 1 < b) {
            dp[i][j][k] += dp[i + 1][j + 1][1];
            dp[i][j][k] += dp[i + 1][j + 1][0];
          }
        } else {
          if (i + a <= n && j + a < b) {
            dp[i][j][k] += dp[i + a][j + a][1];
          }
          if (j + 1 < b) {
            dp[i][j][k] += dp[i + 1][j + 1][0];
          }
          dp[i][j][k] += val;
        }
      }
    }
  }
  mint ans = 1;
  for (int i = 0; i < n; ++i) ans *= 2;
  ans -= dp[0][0][0];
  cout << ans << "\n";
}

