                if(i < n) tmp = LL(tmp) * two[n-i-1] % P;
                Add(ans, tmp);
            }
            else {
                f[i][t][1] = tmp;
                Add(ss[i], f[i][t][1]);
            }
            Add(f[i][t][1], f[i-1][t][1]);
        }

        Add(f[i][i+1][0], ss[i-1]);
        if(i-A >= 0) Sub(f[i][i+1][0], ss[i-A]);

        for(int t = 1; t <= i; ++t) {
