    if(A < B) swap(A, B);

    mvec dq(N + 5, 0);
    dq[0] = 1;
    FOR(i, 1, N + 5){
        dq[i] += dq[i - 1];
        FOR(j, i + B, N + 5) dq[j] += dq[i - 1];
    }

    mvec dp(N, 0);
    REP(i, N){
        mint t = 0;
        if(i < A) t += dq[i];

        FOR(j, 2, A + 1) if(i - j >= 0) t += dp[i - j] * dq[j - 2]; 
        FOR(j, 0, B - 1) if(i + j < N) dp[i + j] += t;
    }
    //vdebug(dq);
    //vdebug(dp);

    mint ans = dp[N - 1];
    FOR(j, 1, A) ans += dp[N - 1 - j] * dq[j];

    ans = pow((mint)2, N) - ans;

    Out(ans);

    return 0;
}

