	for(int i=1; i<A; ++i) dp[0][i]=0;
	sum[0]=0;
	sum[1]=1;
	ds[0][1]=1;
	for(int i=0; i<=N; ++i) ds[i][0]=0;
	for(int i=0; i<N; ++i){
		for(int j=1; j<A; ++j) dp[i+1][j]=dp[i][j-1];
		(dp[i+1][1] += sum[i]+mod-sum[max(0, i-B+1)]) %= mod;
		for(int j=B+1; j<A; ++j){
			(dp[i+1][j] += ds[i+1-j][j-B]) %= mod;
		}
		sum[i+2]=sum[i+1];
		for(int j=0; j<A; ++j) (sum[i+2] += dp[i+1][j]) %= mod;
		for(int j=0; j<min(A, i+1); ++j){
			ds[i+1-j][j+1]=(ds[i+1-j][j]+dp[i+1][j])%mod;
		}
	}
	ll ans=(sum[N+1]+mod-sum[N-B+1])%mod, mult=1;
	for(int i=0; i<N; ++i) (mult *= 2) %= mod;
	for(int i=B+1; i>=A-1; --i){
