	for(int i = 1; i < B; ++i) {
		for(int j = 1; i + j <= B; ++j)
			bin(a) (h[i + j][a][1] += h[i][a][0]) %= P;
		bin(a) (h[i + A][a][0] += h[i][a][1]) %= P;
		for(int j = A + 1; i + j <= B; ++j)
			bin(a) (h[i + j][a][0] += h[i][a][1]) %= P;
	}
