		(f[i][b] += h[i][a][b]) %= P;
	for(int i = 1; i < A; ++i) g2[i]++;
	for(int i = 1; i <= n; ++i) {
		bin(a) (f2[i][a] += f[i][a]) %= P;
		for(int j = 1; i + j <= n; ++j)
			bin(a) bin(b) (f2[i + j][b] += f[i][a] * p2[j - 1]) %= P;
		g[i] = (P + p2[i - 1] - f2[i][1]) % P;
