                if(i and i+j<n) dp1[i+j]+=dp0[i]*dp2[j-1];
                else dp1[i+j]+=dp0[i]*dp2[j];
            }
            for(int j=1; j<b and i+j<=n; ++j)
            {
                if(i and i+j<n) dp0[i+j]+=dp1[i]*dp2[j-1];
                else dp0[i+j]+=dp1[i]*dp2[j];
            }
        }
        cout << mint::pow(2,n)-dp0[n]-dp1[n] << "\n";

    }
};
