void MAIN() {
    scanf("%d%d%d", &N, &A, &B);
    if (A > B) swap(A, B);
    aFa[A] = 1;
    for (int i=A; i<N; i++) {
	// b;
	aFa[i+1] += aFa[i];
	// baaaaa;
	for (int a=A; a+i<=N; a++) aFa[i+a+1] += aFa[i];
    }

    for (int a=A; a<N; a++) aFb[a+1] = 1;
    for (int i=A+1; i<N; i++) {
	// b;
	aFb[i+1] += aFb[i];
	// aaaaab;
	for (int a=A; a+i<=N; a++) aFb[i+a+1] += aFb[i];
    }

    bFb[1] = 1;
    for (int i=1; i<N; i++) {
	// b;
	bFb[i+1] += bFb[i];
	// aaaaab;
	for (int a=A; a+i<=N; a++) bFb[i+a+1] += bFb[i];
    }


    Mint ans = 0;

    for (int a=1; a<A; a++) {
	dp0[a] += 1;
    }
    for (int b=1; b<B; b++) {
	dp1[b] += bFb[b];
	dp1[b] += aFb[b];
    }
    for (int b=B; b<N; b++) {
	dp2[b] += bFb[b];
	dp2[b] += aFb[b];
    }

    ans += aFa[N];
    ans += aFb[N] * 2;
    ans += bFb[N];

    //eprintf("%d\n", aFa[N].geti());
    //eprintf("%d\n", aFb[N].geti());
    //eprintf("%d\n", bFb[N].geti());
    //eprintf("%d\n", ans.geti());

    for (int i=1; i<N; i++) {
	for (int a=1; a<A; a++) {
	    dp0[i+a] += dp1[i];
	}

	for (int b=1; b<B; b++) {
	    dp1[i+b] += dp0[i] * bFb[b];
	}

	for (int b=B; b+i<N; b++) {
	    dp2[i+b] += dp0[i] * bFb[b];
	}

	if (N-i >= B) {
	    ans += dp0[i] * bFb[N-i];
	    ans += dp0[i] * aFb[N-i]; // bFa;
	}

	for (int a=1; a<A; a++) {
	    dp3[i+a] += dp2[i];
	}

	// b;
	dp4[i+1] += dp3[i];
	// a or b;
	dp4[i+1] += dp4[i] * 2;
    }

    ans += dp3[N] + dp4[N];

    printf("%d\n", ans.geti());
}

