const int MAXN = 5010;
LL f[MAXN][MAXN];
int pot[MAXN];
const LL mod = 1e9 + 7;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0); // Remove in problems with online queries!

    pot[0] = 1;
    for (int i = 1; i < MAXN; i++) pot[i] = 2*pot[i-1] % mod;

    int N, A, B;
    cin >> N >> A >> B;
    if (A > B) swap(A, B);
    if (A == 1) {
        cout << pot[MAXN] << "\n";
        return 0;
    }
    vector<LL> pref(MAXN, 0);
    vector<vector<LL>> hardpref(MAXN, vector<LL>(MAXN, 0));
    for (int n = 1; n <= N; n++) {
        for (int k = 0; k < B; k++) {
            f[n][k] += f[n-1][k+1];
            if (k == B-1) f[n][k] += pot[n-1];
            if (k + n >= B and n >= A) f[n][k]++;
            f[n][k] += pref[n-1] - ((n-A >= 0)?pref[n-A]:0);
            // n-1-A, 1+k+A
            // n-z, k+z
            int low = 1+A;
            int high = min(n, B-k-1);
            if (low <= high) f[n][k] += hardpref[n-low][k+low];
            if (high != n) f[n][k] -= hardpref[n-high-1][k+high+1];
            // for (int t = A; 1+t <= min(n, B-k-1); t++) {
                // f[n][k] += f[n-1-t][1+k+t];
            // }
            int z = max(B-k, A+1);
            if (n-z+1 >= 0) f[n][k] += pot[n-z+1]-1;
            f[n][k] %= mod;
            hardpref[n][k] = (hardpref[n-1][k+1] + f[n][k]) % mod;
        }
        pref[n+1] = (pref[n] + f[n][1]) % mod;
    }   
    // dbg(f[1][2]);
    if (f[N][0] < 0) f[N][0] += mod;
    cout << f[N][0] << "\n";
}

