template<typename T>
struct Polynomial : vector<T> {
  using vector<T>::vector;
  using P = Polynomial;
  int n = (*this).size();

  P operator-() const {
    P res(n);
    rep(i, n) res[i] = -(*this)[i];
    return res;
  }
  P &operator+=(const P q) {
    rep(i, n) if (i < q.size()) (*this)[i] += q[i];
    return *this;
  }
  P &operator-=(const P q) {
    rep(i, n) if (i < q.size()) (*this)[i] -= q[i];
    return *this;
  }
  P &operator*=(const P q) { // dense
    drep(i, n) {
      (*this)[i] *= q[0];
      rep2(j, 1, q.size()) if (j <= i) (*this)[i] += (*this)[i-j] * q[j];
    }
    return *this;
  }
  template<typename T2>
  P &operator*=(map<int, T2> q) { // sparse
    drep(i, n) {
      (*this)[i] *= q[0];
      for (auto &[j, b] : q) if (j > 0 && j <= i) (*this)[i] += (*this)[i-j] * b;
    }
    return *this;
  }
  P &operator/=(const P q) { // dense
    rep(i, n) {
      rep2(j, 1, q.size()) if (j <= i) (*this)[i] -= (*this)[i-j] * q[j];
      (*this)[i] /= q[0];
    }
    return *this;
  }
  template<typename T2>
  P &operator/=(map<int, T2> q) { // sparse
    rep(i, n) {
      for (auto &[j, b] : q) if (j > 0 && j <= i) (*this)[i] -= (*this)[i-j] * b;
      (*this)[i] /= q[0];
    }
    return *this;
  }
  template<typename T2> P operator+(const T2 q) const { return P(*this) += q; }
  template<typename T2> P operator-(const T2 q) const { return P(*this) -= q; }
  template<typename T2> P operator*(const T2 q) const { return P(*this) *= q; }
  template<typename T2> P operator/(const T2 q) const { return P(*this) /= q; }
};


int main() {
  ll n, a, b; cin >> n >> a >> b;
  if (a > b) swap(a, b);

  Polynomial<mint> p(n+1), q(n+1), f(n+1);
  map<int, mint> d({{0, 1}, {1, -1}});
  p[a] = 1;
  p /= d;
  q[1] = 1;
  q /= d;
  f[1] = 1;
  f[a] -= 1;
  f /= d;

  auto r = -p * q;
  r[0] += 1;

  auto g2 = q / r;
  auto g1 = q;
  g1[0] += 1;
  g1 /= r;
  auto g0 = p + q;
  g0[0] += 2;
  g0 /= r;

  rep2(i, b, n+1) g0[i] = 0;
  rep2(i, b, n+1) g1[i] = 0;
  rep2(i, b, n+1) g2[i] = 0;

  auto h = f;
  h *= g1 * g1;
  auto h0 = -f * g2;
  h0[0] += 1;
  h /= h0;
  h += g0;

  mint ans = mint(2).pow(n) - h[n];
  cout << ans << "\n";
  return 0;
}

