long long naive(int N, int A, int B)
{
    set<string> S;
    S.insert(string(N, '0'));

    while (true)
    {
        bool up = false;
        for (string s: S)
        {
            for (int i=0; i<=N-A; i++)
            {
                for (int j=0; j+A<=N; j++)
                {
                    string t = s;
                    for (int k=0; k<A; k++)
                        t[j+k] = '0';
                    if (S.count(t)==0)
                    {
                        up = true;
                        S.insert(t);
                    }
                }
            }
            for (int i=0; i<=N-B; i++)
            {
                for (int j=0; j+B<=N; j++)
                {
                    string t = s;
                    for (int k=0; k<B; k++)
                        t[j+k] = '1';
                    if (S.count(t)==0)
                    {
                        up = true;
                        S.insert(t);
                    }
                }
            }
        }
        if (!up)
            break;
    }
    //for (string s: S)
    //    cout<<s<<endl;
    return S.size();
}

long long solve(int N, int A, int B)
{
    long long M = 1000000007;

    if (A<B)
        swap(A, B);

    if (B==1)
    {
        long long ans = 1;
        for (int i=0; i<N; i++)
            ans = ans*2%M;
        return ans;
    }

    vector<long long> T1(B);
    vector<long long> S1(N+1);
    T1[0] = 1;
    S1[0] = 1;
    for (int i=0; i<N; i++)
    {
        vector<long long> P(B);
        T1.swap(P);
        for (int j=0; j<B; j++)
        {
            T1[0] += P[j];
            T1[0] %= M;
            if (j+1<B)
            {
                T1[j+1] += P[j];
                T1[j+1] %= M;
            }
        }
        for (int j=0; j<=B-2; j++)
        {
            S1[i+1] += T1[j];
            S1[i+1] %= M;
        }
    }

    vector<long long> T2(N+1);
    vector<long long> S2(N+1);
    T2[0] = 1;
    S2[0] = 1;
    for (int i=0; i<N; i++)
    {
        vector<long long> P(N+1);
        T2.swap(P);
        for (int j=0; j<=N; j++)
        {
            if (j>=B || j==0)
            {
                T2[0] += P[j];
                T2[0] %= M;
            }
            if (j<N)
            {
                T2[j+1] += P[j];
                T2[j+1] %= M;
            }
        }
        for (int j=B; j<=N; j++)
        {
            S2[i+1] += T2[j]+T2[0];
            S2[i+1] %= M;
        }
    }

    long long ans = 0;
    for (int l=A; l<=N; l++)
    {
        for (int a=0; a+l+4<=N; a++)
        {
            ans += S2[l]*S1[a]%M*S1[N-l-a-4]%M;
            ans %= M;
        }
        if (l+2<=N)
        {
            ans += S2[l]*S1[N-l-2]*2%M;
            ans %= M;
        }
        if (l==N)
        {
            ans += S2[l];
            ans %= M;
        }
    }
    return ans;
}

