		res[i+1]%=mod;

		//add 1
		for(int j=0;j<n;j++){
			if((j+1)<b){
				dp[i+1][j+1]+=dp[i][j];
				dp[i+1][j+1]+=dp2[i][j];
				dp[i+1][j+1]%=mod;
			}
		}
		res[i+1]+=dp[i][b-1];
		res[i+1]+=dp2[i][b-1]*2;
		res[i+1]%=mod;

		//add 0
		for(int j=0;j<n;j++){
			if((j+1)<b){
				dp2[i+1][j+1]+=dp2[i][j];
				dp2[i+1][j+1]%=mod;
