		cnt_segB[i] = cnt_segB[i-1];
		for(int j = A; j <= i-2; j++) cnt_segB[i] += cnt_segB[i-1-j];
		cnt_segB[i] %= mod;
	}
	vector<cat> cnt_segB_bor(B, 0);
	cnt_segB_bor[0] = 1;
	for(int i = 1; i < B; i++) {
		cnt_segB_bor[i] = cnt_segB_bor[i-1];
