bool used1[MN];
Mint dp1[MN];
Mint solve1(int p) {
    if (n + 1 <= p) return Mint(0);
    if (used1[p]) return dp1[p];
    used1[p] = true;
    return dp1[p] = solve(p, 1) + solve1(p + 1);
}

bool used2[MN][MN];
Mint dp2[MN][MN];
Mint solve2(int p, int q) {
    if (n + 1 <= p) return Mint(0);
    if (q >= b) return Mint(0);

    if (used2[p][q]) return dp2[p][q];
    used2[p][q] = true;
    return dp2[p][q] = solve(p, q) + solve2(p + 1, q + 1);
}

Mint solve(int p, int q) {
    if (q >= b) return Mint(0);
    if (p == n) return Mint(1);
    if (used[p][q]) return dp[p][q];
    used[p][q] = true;

    Mint ans = Mint(0);

    // 000
    if (n - p < a || q + (n - p) < b) ans += 1;

    // 1
    ans += solve(p + 1, q + 1);

    // 01, 001, ...
    /*for (int r = p + 1; r < min(n, p + a); r++) {
        ans += solve(r + 1, 1);
    }*/
    ans += solve1(p + 2) - solve1(p + a + 1);


/*    for (int r = p + a; r < n; r++) {
        int nq = q + r - p + 1;
        ans += solve(r + 1, nq);
    }*/
    ans += solve2(p + a + 1, q + a + 1);

    return dp[p][q] = ans;
};

