		for(int fl = 0;fl < 2;fl++){
			for(int lst = 0;lst < 2;lst++){
				dp[n][k][fl][lst] = (n - k) >= b || fl;
				pref[n][k][fl][lst] = dp[n][k][fl][lst];
				if(n == k){
					pos_pref[n][fl][lst] = dp[n][n][fl][lst];
				}
			}
		}
