ll pot (ll b, int p) {
	ll res = 1;
	while (p) {
		if (p & 1) (res *= b) %= mod;
		(b *= b) %= mod;
		p /= 2;
	}
	return res;
}

void sum (int &a, int b) {
	a += b;
	if (a >= mod) a -= mod;
}

int dp (int i, int j, int f);

int dp2 (int i, int j) {
	if (i < 0) return 0;

	int &res = mem2[i][j];
	if (res != -1) return res;
	res = 0;

	sum(res, dp(i, j, 1));
	sum(res, dp2(i - 1, j + 1));

	return res;
}

int dp (int i, int j, int f) {
	if (f == 0 && j >= a) return 0;
	if (f == 1 && j >= b) return 0;
	if (!i) return 1;

	int &res = mem[i][j][f];
	if (res != -1) return res;
	res = 0;

	sum(res, dp(i - 1, j + 1, f));
	sum(res, dp(i - 1, 1, f ^ 1));

	if (i == n || (j && f)) {
		sum(res, dp2(i - a - 1, j + a + 1));

		if (j && f && i >= a)
			sum(res, dp(0, j + i, f));
	}

	return res;
}

int main () {
