using mint = modint<1000000007>;
int main() {
	int N, A, B;
	cin >> N >> A >> B;
	if (A < B) swap(A, B);
	vector<vector<mint> > dp(N + 1, vector<mint>(2 * N + 1)); // (pos, cont)
	for (int i = 0; i <= N; ++i) {
		mint g = mint(2).pow(N - i);
		for (int j = A; j <= 2 * N; ++j) {
			dp[i][j] = g;
		}
	}
	vector<mint> sum_line1(N + 2);
	sum_line1[N] = dp[N][1];
	vector<vector<mint> > sum_line45(N + 2, vector<mint>(2 * N + 2));
	for (int i = N; i >= 0; --i) {
		for (int j = 2 * N; j >= A; --j) {
			sum_line45[i][j] = sum_line45[i + 1][j + 1] + dp[i][j];
		}
	}
	for (int i = N - 1; i >= 0; --i) {
		for (int j = 0; j < A; ++j) {
			// next = A
			dp[i][j] += dp[i + 1][j + 1];
			// next = BA, BBA, BBBA, ...
			if (min(N - i, B) >= 2) {
				dp[i][j] += sum_line1[i + 2] - sum_line1[i + min(N - i, B) + 1];
			}
			if (N - i >= B + 1) {
				dp[i][j] += sum_line45[i + (B + 1)][j + (B + 1)] - sum_line45[i + (N - i) + 1][j + (N - i) + 1];
			}
			// next = BBBBB...
			if (N - i >= B && j + N - i >= A) {
				dp[i][j] += 1;
			}
			sum_line45[i][j] = sum_line45[i + 1][j + 1] + dp[i][j];
		}
		sum_line1[i] = sum_line1[i + 1] + dp[i][1];
	}
	cout << dp[0][0].get() << endl;
	return 0;
}

