int mem[2][5010][5010];
int smem[5010][5010];
num dp(int, int, bool);
num dpsum(int n, int l) {
	if (n < 0) return 0;
	if (smem[n][l] != -1) return smem[n][l];
	smem[n][l] = (dp(n, l, false) + dpsum(n - 1, l + 1)).value;
	return smem[n][l];
}
int md[5010];
num dpref(int n) {
	if (n < 0) return 0;
	if (md[n] != -1) return md[n];
	md[n] = (dp(n, 0, false) + dpref(n - 1)).value;
	return md[n];
}
num dp(int n, int l, bool can_one) {
	if (n < 0) return 0;
	if (n == 0) return l >= a;
	if (mem[can_one][n][l] != -1) return mem[can_one][n][l];
	num rez = 0;
	if (l >= a) {
		rez = num(2).pow(n + can_one - 1);
	} else {
		if (can_one) {
			rez += dpref(n - 1) - dpref(n - b);
			rez += dpsum(n - b, l + b);
		}
		rez += dp(n - 1, l + 1, true);
	}
	mem[can_one][n][l] = rez.value;
	return rez;
}

