inline void Add(int &x, int y) {
	x += y;
	if (x >= mod) { x -= mod; }
}
inline void Sub(int &x, int y) {
	x -= y;
	if (x < 0) { x += mod; }
}
inline void Mul(int &x, int y) {
	x = (ll)x * y % mod;
}

int PowMod(int a, int n) {
	int r = 1;
	while (n) {
		if (n & 1) { r = (ll)r * a % mod; }
		n >>= 1;
		a = (ll)a * a % mod;
	}
	return r;
}

int dp0[kMaxN][kMaxN];
int dp1[kMaxN][kMaxN];
int sum_dp0[kMaxN];
int sum_dp1[kMaxN];

