	sum_dp0[0] = 1;
	for (int n = 1; n <= N; ++n) {
		for (int g = 0; g < B && g <= n; ++g) {
			if (n && g) {
				Add(dp0[n][g], dp0[n-1][g-1]);
				Add(dp0[n][g], dp1[n-1][g-1]);
			}
			if (g == 0) {
				for (int k = 1; k < A && n - k >= 0; ++k) {
					Add(dp1[n][0], sum_dp0[n - k]);
				}
			} else if (g == A) {
				dp1[n][A] = dp0[n - A][0];
