			debug() << imie(n) << imie(g) << imie(dp0[n][g]) << imie(dp1[n][g]);

			Add(sum_dp0[n], dp0[n][g]);
			Add(sum_dp1[n], dp1[n][g]);
		}
	}

	int res = PowMod(2, N);
