class Solver2 {
 public:
  int n, a, b;
  vector<vector<vector<LL>>> dp;
  Solver2(int n, int a, int b) : n(n), a(a), b(b), dp(max(100, n + 1), vector<vector<LL>>(max(100, n + 1), vector<LL>(2))){};

  void solve() {
    if (a < b) swap(a, b);
    dp[0][0][0] = LL(1);
    dp[2][1][0] = LL(MOD - 1);
    auto ans = Modint<>(0);
    for (int i = 0; i <= n; ++i) {
      if (i > 0) dp[i][1][0] += dp[i - 1][1][0];
      dp[i][1][0] %= MOD;
      for (int j = 0; j < a; ++j) {
        if (i + b <= n && j + b <= n) dp[i + b][j + b][1] += dp[i][j][0];
        if (i + 2 <= n) dp[i + 2][1][0] += dp[i][j][0];
        if (i + b + 1 <= n) dp[i + b + 1][1][0] -= dp[i][j][0];
        if (i + 1 <= n && j + 1 <= n) dp[i + 1][j + 1][0] += dp[i][j][0];
        if (i + 1 <= n && j + 1 <= n) dp[i + 1][j + 1][1] += dp[i][j][1];
        if (i + 1 <= n && j + 1 <= n) dp[i + 1][j + 1][0] += dp[i][j][1];

        if (i + b <= n && j + b <= n) dp[i + b][j + b][1] %= MOD;
        if (i + 2 <= n) dp[i + 2][1][0] %= MOD;
        if (i + b + 1 <= n) dp[i + b + 1][1][0] += MOD;
        if (i + b + 1 <= n) dp[i + b + 1][1][0] %= MOD;
        if (i + 1 <= n && j + 1 <= n) dp[i + 1][j + 1][0] %= MOD;
        if (i + 1 <= n && j + 1 <= n) dp[i + 1][j + 1][1] %= MOD;
      }
      auto cf = Modint<>(2).mpow(n - i);
      for (int j = a; j <= n; ++j) {
        ans += (dp[i][j][0] + dp[i][j][1]) * cf;
      }
    }
    cout << ans << endl;
  }
};
