    coef[1] = 1;
    for (const usize i : rep(2, n + 2)) {
      coef[i] = coef[i - 1];
      for (const usize j : rep(b + 1, n + 2)) {
        if (i < j)
          break;
        coef[i] += coef[i - j];
      }
    }

    std::vector<mint> ze(n + 1, 0), on(n + 1, 0);
