        static value_t op(value_t lhs,value_t rhs){ return max(lhs,rhs);}
    };
    struct Update{
        using update_t = int;
        static update_t op(update_t lhs,update_t rhs){ return lhs+rhs;}
    };
    static Monoid::value_t evaluate(Monoid::value_t v,Update::update_t u){ return make_pair(v.first+u,v.second);}
};

struct MUStructMin{
    struct Monoid{
        using value_t=pair<int,int>;
        static constexpr value_t E(){return make_pair(INF,INF);}
        static value_t op(value_t lhs,value_t rhs){ return min(lhs,rhs);}
    };
    struct Update{
        using update_t = int;
        static update_t op(update_t lhs,update_t rhs){ return lhs+rhs;}
    };
    static Monoid::value_t evaluate(Monoid::value_t v,Update::update_t u){ return make_pair(v.first+u,v.second);};
