void solve() {
	cin >> N >> A >> B;
	if(A < B) swap(A, B);
	fp[0] = 1;
	rep(i, 1, N + 1) {
		ADD(fp[i], fp[i - 1]);
		rep(j, 0, N + 1) {
			if(i - j >= B + 1) ADD(fp[i], fp[j]);
		}
	}

	// debug(vl(fp, fp + N + 1));
	ll res = 0;
	dp[0][0] = 1;
