			int jump = j - i;
			if(jump < A) ADD(dp[j][1], dp[i][0] * fp[jump - (i != 0)] % mod);
			if(jump < B) ADD(dp[j][0], dp[i][1]);
		}
		// debug(i, dp[i][0], dp[i][1], dp[i][1] * fp[N - i]);
