using mint = Fp<MOD>;

void solve() {
	int n, a, b; cin >> n >> a >> b;
    if(a == 1 || b == 1) {
        cout << modpow((mint)2, n) << endl;
        return;
    }
    if(a < b) swap(a, b);
    static mint dp[5050][5050][2][2][3];
    rep(i, 5050) {
        rep(j, 5050) {
            rep(k, 2) {
                rep(l, 2) {
                    rep(m, 2) {
                        dp[i][j][k][l][m] = 0;
                    }
                }
            }
        }
    }
    dp[0][0][0][0][0] = 1;
    rep(i, n) {
        rep(j, n + 1) {
                rep(l, 2) {
                    rep(m, 3) {
                        int nl, nm;
                        nl = (l || (j + 1 >= a)), nm = m;
                        dp[i + 1][j + 1][0][nl][nm] += dp[i][j][0][l][m];
                        nl = l, nm = m;
                        dp[i + 1][1][1][nl][nm] += dp[i][j][0][l][m];
                        nl = l, nm = m;
                        dp[i + 1][j + 1][1][nl][nm] += dp[i][j][1][l][m];
                        nl = l;
                        if(i - j >= a && j <= b - 1) nm = (m == 0 ? 1 : 2);
                        else if(n - i >= a && j <= b - 1) nm = (m == 0 ? 1 : 2);
                        else if(j <= b - 1) nm = 2;
                        else nm = m;
                        dp[i + 1][1][0][nl][nm] += dp[i][j][1][l][m];
                    }
                }
        }
    }
    mint ans = 0;
    rep(j, n + 1) {
        rep(m, 2) {
            rep(k, 2) {
                ans += dp[n][j][k][1][m];
            }
            ans += dp[n][j][0][0][m];
        }
        if(j >= b) {
            ans += dp[n][j][1][0][0] + dp[n][j][1][0][1];
        }
        else {
            if(n - j >= a) ans += dp[n][j][1][0][0];
        }
    }
    cout << ans << endl;
}

