    {
        REP(a, A + 1) dp0[i + 1][a] += dp0[i][a];
        dp1[i + 1] += dp1[i] * 2;
        REP(a, A)
        {
            // a
            if (a == A - 1)
            {
                dp1[i + 1] += dp0[i][a] + dpbsuc[i][a];
            }
            else
            {
                dp0[i + 1][a + 1] += dp0[i][a] + dpbsuc[i][a];
                dp0[i + 2][a + 1] -= dp0[i][a] + dpbsuc[i][a];
            }

        }
        // b
        REP(a, A)
        {
            if (a == A - 1)
            {
                dp1[i + 1] += dpbsuc[i][a];
            }
            else
            {
                dpbsuc[i + 1][a + 1] += dpbsuc[i][a];
            }

            if (i + B <= N)
            {
                if (a + B < A) dpbsuc[i + B][a + B] += dp0[i][a];
                else
                {
                    dp1[i + B] += dp0[i][a];
                }
            }

            if (i + B + 1 <= N) dp0[i + B + 1][1] -= dp0[i][a];
            dp0[i + 2][1] += dp0[i][a];

            // FOR(j, i + 2, i + B + 1) if (j <= N) dp0[j][1] += dp0[i][a];
