        REP(len, 1, n + 1) {
            REP(j, 1, min (len, a - 1) + 1) {
                // 0
                dp[len][0] += dp[len - j][1];
                dp[len][0] %= mod;
            }
            REP(j, 1, min (len, b - 1) + 1) {
                // 1
                dp[len][1] += dp[len - j][0] * (j == 1 ? 1 : (trans[j - 2][1] + trans[j - 2][0])) % mod;
                dp[len][1] %= mod;
                if (len == n) {
                    dp[len][1] += (trans[j - 1][0]) * dp[len - j][0];
                }
            }
            if (len < b) {
                dp[len][1] += trans[len - 1][0];
            }
        }
        ll tot = dp[n][0] + dp[n][1];
        tot = fast (2, n, mod) - tot;
        cout<<MOD (tot, mod);
    }
}


