#include <bits/stdc++.h>
using namespace std;

const int N = 5005;

int n, A, B;

int dp1[N][2];
int dp2[N][2][2];
int dp3[N][2][2];
int mod = 1e9 + 7;

int solve1(int baki, int ki) {
    if(baki < 0) return 0;
    if(baki == 0) return ki == 1;
    if(dp1[baki][ki] != -1) return dp1[baki][ki];
    int ans = 0;
    if(ki == 0) {
        for(int x = 1; x < A && x <= baki; x++) {
            ans += solve1(baki - x, ki ^ 1);
            ans %= mod;
        }
    }
    else {
        for(int x = B; x <= baki; x++) {
            ans += solve1(baki - x, ki ^ 1);
            ans %= mod;
        }
    }
    return dp1[baki][ki] = ans;
}

int solve2(int baki, int ki, bool nichi) {
    if(baki < 0) return 0;
    if(baki == 0) return nichi;
    if(dp2[baki][ki][nichi] != -1) return dp2[baki][ki][nichi];
    int ans = 0;
    if(ki == 1) {
        for(int x = 1; x <= baki; x++) {
            ans += solve2(baki - x, ki ^ 1, nichi);
            ans %= mod;
        }
    }
    else {
        for(int x = 1; x <= baki; x++) {
            ans += solve2(baki - x, ki ^ 1, nichi|(x >= A));
            ans %= mod;
        }
    }
    return dp2[baki][ki][nichi] = ans;
}

int solve3(int baki, int ki, bool nichi) {
    if(baki < 0) return 0;
    if(baki == 0) return nichi;
    if(dp3[baki][ki][nichi] != -1) return dp3[baki][ki][nichi];
    int ans = 0;
    if(ki == 1) {
        for(int x = 1; x < B; x++) {
            ans += solve3(baki - x, ki ^ 1, nichi);
            ans %= mod;
        }
    }
    else {
        for(int x = 1; x <= baki; x++) {
            int t = solve3(baki - x, ki ^ 1, nichi|(x >= A));
            if(t >= A) {
                t = 1ll*t*solve1(x, 0)%mod;
            }
            ans += t;
            ans %= mod;
        }
    }
    return dp3[baki][ki][nichi] = ans;
}

int main() {
    // freopen("in.txt", "r", stdin);
    ios::sync_with_stdio(0);
    cin.tie(0);

    memset(dp1, -1, sizeof dp1);
    memset(dp2, -1, sizeof dp2);
    memset(dp3, -1, sizeof dp3);

    cin >> n >> A >> B;
    if(B > A) swap(A, B);
    int ans = solve2(n,0,0) + solve2(n,1,0);
    ans %= mod;
    ans += solve3(n,0,0);
    ans %= mod;
    ans += solve3(n,1,0);
    ans %= mod;
    cout << ans << endl;

    return 0;
}

