    rep(i, N) {
        for (int j = 1; i + j <= N; j++) { dp0[i + j][0] += dp0[i][1]; }
        for (int j = B; i + j <= N; j++) { dp0[i + j][1] += dp0[i][0]; }
    }
    dp1[0][0][0] = 1, dp1[0][1][0] = 1;
    rep(i, N) {
        for (int j = 1; j < A && i + j <= N; j++) {
            dp1[i + j][0][0] += dp1[i][1][0];
            dp1[i + j][0][1] += dp1[i][1][1];
        }
        for (int j = 1; j < B && i + j <= N; j++) {
            dp1[i + j][1][0] += dp1[i][0][0];
            dp1[i + j][1][1] += dp1[i][0][1];
            dp1[i + j][1][0] += dp1[i][2][0];
            dp1[i + j][1][1] += dp1[i][2][1];
        }
        for (int j = A; i + j <= N; j++) {
            if (i != 0 && i + j != N) {
                dp1[i + j][2][1] += dp1[i][1][0] * dp0[j - 1][0];
                dp1[i + j][2][1] += dp1[i][1][1] * dp0[j - 1][0];
            } else if (j != N) {
                dp1[i + j][2][1] += dp1[i][1][0] * dp0[j][0];
                dp1[i + j][2][1] += dp1[i][1][1] * dp0[j][0];
            } else {
                dp1[i + j][2][1] += dp1[i][1][0] * (dp0[j][0] + dp0[j][1]);
                dp1[i + j][2][1] += dp1[i][1][1] * (dp0[j][0] + dp0[j][1]);
            }
        }
    }
    cout << dp1[N][0][1] + dp1[N][1][1] + dp1[N][2][1] << endl;
}

