using namespace std;

const int mod = 1000000007;
int dp[5001];
int dp2[5001];
int dp3[5001][2];

int main() {
    int n, a, b, ans, i, j, k;

    scanf("%d %d %d", &n, &a, &b);

    if (a > b) swap(a, b);

    dp[1] = 1;

    for (i = 1; i < n; i++) {
        dp[i + 1] += dp[i];
        if (dp[i + 1] >= mod) dp[i + 1] -= mod;

        for (j = a + 1; i + j <= n; j++) {
            dp[i + j] += dp[i];
            if (dp[i + j] >= mod) dp[i + j] -= mod;
        }
    }

    dp2[0] = 1;

    for (i = 0; i < n; i++) {
        dp2[i + 1] += dp2[i];
        if (dp2[i + 1] >= mod) dp2[i + 1] -= mod;

        for (j = a + 1; i + j <= n; j++) {
            dp2[i + j] += dp2[i];
            if (dp2[i + j] >= mod) dp2[i + j] -= mod;
        }
    }

    for (i = 0; i < b; i++) dp3[i][1] = dp2[i];

    for (i = 0; i < n; i++) {
        for (j = 0; j < 2; j++) {
            if (dp3[i][j] == 0) continue;

            if (j != 0) {
                for (k = 1; k < a && i + k <= n; k++) {
                    dp3[i + k][0] += dp3[i][j];
                    if (dp3[i + k][0] >= mod) dp3[i + k][0] -= mod;
                }
            }

            if (j != 1) {
                for (k = 1; k < b && i + k < n; k++) {
                    dp3[i + k][1] += (long long)dp3[i][j] * dp[k] % mod;
                    if (dp3[i + k][1] >= mod) dp3[i + k][1] -= mod;
                }

                if (n - i < b) {
                    dp3[n][1] += (long long)dp3[i][j] * dp2[n - i] % mod;
                    if (dp3[n][1] >= mod) dp3[n][1] -= mod;
                }
            }
        }
    }

    ans = 1;
    for (i = 0; i < n; i++) ans = ans * 2 % mod;

    for (i = 0; i < 2; i++) {
        ans -= dp3[n][i];
        if (ans < 0) ans += mod;
    }

    printf("%d\n", ans);

    return 0;
}

