const int vmax=(1<<21)+10;
mint fact[vmax],finv[vmax],invs[vmax];
void initfact(){
	fact[0]=1;
	rng(i,1,vmax){
		fact[i]=fact[i-1]*i;
	}
	finv[vmax-1]=fact[vmax-1].inv();
	for(int i=vmax-2;i>=0;i--){
		finv[i]=finv[i+1]*(i+1);
	}
	for(int i=vmax-1;i>=1;i--){
		invs[i]=finv[i]*fact[i-1];
	}
}
mint choose(int n,int k){
	return fact[n]*finv[n-k]*finv[k];
}
mint binom(int a,int b){
	return fact[a+b]*finv[a]*finv[b];
}
mint catalan(int n){
	return binom(n,n)-(n-1>=0?binom(n-1,n+1):0);
}

const int nmax=5010;
mint f[nmax][2];
mint dp[nmax][2];

signed main(){
	cin.tie(0);
	ios::sync_with_stdio(0);
	cout<<fixed<<setprecision(20);

	int n,a,b;cin>>n>>a>>b;
	if(a>b)swap(a,b);

	mint ans;

	if(b>=2){
		f[0][0]=1;
		f[0][1]=1;
		rng(i,1,n+1){
			rep(k,2){
				rng(j,1,i+1){
					if(k==0&&j<a)continue;
					f[i][k]+=f[i-j][k^1];
				}
			}
		}

		vc<mint> g(n+1);
		g[0]=1;
		rng(i,1,n+1)g[i]=f[i][0]+f[i][1];

		rng(i,1,n){
			if(i<a)dp[i][0]+=1;
			rng(j,1,min(i,a))dp[i][0]+=dp[i-j][1];
			if(i<b)dp[i][1]+=g[i-1];
			rng(j,1,min(i,b)){
				mint w=j==1?1:g[j-2];
				dp[i][1]+=dp[i-j][0]*w;
			}
			if(n-i<b)ans+=dp[i][0]*g[n-i-1];
			if(n-i<a)ans+=dp[i][1];
		}
	}

	cout<<mint(2).pow(n)-ans<<endl;
}

