	}

	dp[1][a - 1][0] = 1;
	dp[1][b - 1][1] = 1;
	dp[a][0][2] = 1;

	for(int i = 1; i < n; ++i){
		for(int c = 0; c < 3; ++c)
			add(dp[i + 1][0][2], dp[i][0][c]);

		for(int c = 0; c < 3; ++c)
			if(i + 1 < b)
				add(dp[i + 1][b - i - 1][1], dp[i][0][c]);
			else
				add(dp[i + 1][0][2], dp[i][0][c]);

		for(int j = 1; j < b && n - i >= j; ++j){
			{	//c = 0
				if(j > 1)
					add(dp[i + 1][j - 1][0], dp[i][j][0]);
				add(dp[i + 1][b - 1][1], dp[i][j][0]);
			}

			{	//c = 1
				add(dp[i + 1][j - 1][1], dp[i][j][1]);
				if(i + a <= n)
					add(dp[i + a][max(j - a, 0)][2], dp[i][j][1]);
				add(dp[i + 1][a - 1][0], dp[i][j][1]);
			}

			{	//c = 2
				add(dp[i + 1][j - 1][2], dp[i][j][2]);
				add(dp[i + 1][j - 1][1], dp[i][j][2]);
			}
		}
	}

	int ans = 0;
	for(int c = 0; c < 3; ++c)
		add(ans, dp[n][0][c]);
	printf("%d\n", ans);
	return 0;
}

