num dp(int n, int l, bool can_one) {
	if (n < 0) return 0;
	if (n == 0) return l >= a;
	if (mem[can_one][n][l] != -1) return mem[can_one][n][l];
	num rez = 0;
	if (l >= a) {
		rez = num(2).pow(n + can_one - 1);
	} else {
		if (can_one) {
			for (int ones = 1; ones <= n; ones++) {
				if (ones < b) {
					rez += dp(n - ones, 0, false);
				} else {
					rez += dp(n - ones, l + ones, false);
				}
			}
		}
		rez += dp(n - 1, l + 1, true);
	}
	mem[can_one][n][l] = rez.value;
	return rez;
}

