mint dp3[5100];
bool used3[5100];
mint func3(int n)
{
	if (n <= 0) {
		return 1_mi;
	}

	auto& memo = dp3[n];
	if (used3[n]) {
		return memo;
	}
	used3[n] = true;

	memo = func3(n - 1);
	for (int c = B; c <= n; c++)
	{
		memo += func3(n - c - 1);
	}
	return memo;
}


