int M = 1000000007;

int main() {
    int n, a, b; cin >> n >> a >> b;
    if (a > b) swap(a, b);

    long long ans = 1;
    for (int i = 0; i < n; i++) ans = ans * 2 % M;
    if (a > 1) {
        long long dp[n][3][b];
        memset(dp, 0, sizeof dp);
        dp[0][0][1] = 1;
        dp[0][1][1] = 0;
        dp[0][2][1] = 1;
        for (int i = 1; i < n; i++) {
            dp[i][0][1] = accumulate(dp[i-1][2]+1, dp[i-1][2]+b, 0LL) % M;
            for (int j = 2; j < a; j++) dp[i][0][j] = dp[i-1][0][j-1];
            if (i+1 == a) dp[i][1][a] = 1;
            for (int j = a+1; j < b; j++)
                dp[i][1][j] = (dp[i-1][1][j-1] + (i >= a ? dp[i-a][2][j-a] : 0)) % M;
            dp[i][2][1] = accumulate(dp[i-1][0]+1, dp[i-1][0]+a, 0LL) % M;
            for (int j = 2; j < b; j++)
                dp[i][2][j] = (dp[i-1][1][j-1] + dp[i-1][2][j-1]) % M;
        }
        ans -= accumulate(dp[n-1][0]+1, dp[n-1][0]+a, 0LL) % M;
        ans -= accumulate(dp[n-1][1]+1, dp[n-1][1]+b, 0LL) % M;
        ans -= accumulate(dp[n-1][2]+1, dp[n-1][2]+b, 0LL) % M;
    }
    cout << (ans % M + M) % M << endl;
}

