void solve()
{
    int n;
    cin>>n;
    int a, b;
    cin>>a>>b;
    if (a>b) swap(a, b);

    vector<vector<vector<int>>> block(2, vector<vector<int>>(2, vector<int>(b)));

    for (int len = 1; len<b; len++)
    {
        for (int last = 0; last<2; last++)
            for (int first = 0; first<2; first++)
            {
                if (first == last)
                {
                    if (last ==1|| len>=a) block[first][last][len] = 1;
                }
                if (last == 0)
                {
                    for (int lenlast = a; lenlast<len; lenlast++) block[first][last][len] = add(block[first][last][len], block[first][1-last][len - lenlast]);
                }
                else
                {
                    for (int lenlast = 1; lenlast<len; lenlast++) block[first][last][len] = add(block[first][last][len], block[first][1-last][len - lenlast]);
                }
            }
    }


    //cout<<"Here"<<endl;

    vector<int> dp1(n+1); //start with white
    vector<int> dp2(n+1); //start with block
    for (int i = 1; i<=n; i++)
    {
        if (i<a) dp1[i] = 1;
        for (int j = 1; j<min(a, i); j++) dp1[i] = add(dp1[i], dp2[i-j]);
        for (int j = 1; j<min(b, i); j++) dp2[i] = add(dp2[i], mul(block[1][1][j], dp1[i-j]));
    }

    ll res = 0;
    for (int pref = 0; pref<b; pref++)
        for (int suf = 0; suf<b; suf++) if (pref+suf<n)
        {
            int P, S;
            if (pref==0) P = 1;
            else P = add(block[0][1][pref], block[1][1][pref]);
            if (suf==0) S = 1;
            else S = add(block[1][0][suf], block[1][1][suf]);
            res = add(res, mul(mul(P, S), dp1[n-pref-suf] ));
        }

    cout<<sub(po(2, n), res);
}

int main() {
    ios_base::sync_with_stdio(0);
    cin.tie(nullptr);

    int t = 1;
    //cin>>t;
    while(t--) solve();


}

