int main(){
	scanf("%d%d%d",&n,&a,&b);
	int ans=1;
	for(int i=1;i<=n;i++)ans=2LL*ans%mod;
	if(a<b)swap(a,b);
	f[0][0]=sf[0][0]=sum[0]=1;
	for(int i=1;i<=n;i++){
		sum[i]=sum[i-1];
		for(int j=0;j<=i&&j<a;j++){
			if(j==0){
				f[i][j]=(i<b);
				if(i>=2)Add(f[i][j],(sum[i-2]-(i-b-1<0?0:sum[i-b-1])+mod)%mod);
